export const Validate = (state)=>{
    let errors = {
      email:'',
      password:''
    }

    if(formContext.state.email.length>0){
      if (!/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/.test(formContext.state.email))
      {
        errors.email = 'Email Is Invalidated';
      } 
    } else {
      errors.email = 'Email Address Is Required'
    }
    if(formContext.state.password.length===0){
      errors.password = 'Password Is Required';
    } else if(formContext.state.password.length<=6){
      errors.password = 'Password Length Must Be Greater Than 6';
    } 
    return errors;
