import React, {useState,useContext} from 'react';
import { Modal,Button,Form } from 'react-bootstrap';
import {FormContext} from '../../context';

export const Login = () => {
    const [show, setShow] = useState(false);
    const [formErrors,setErrors] = useState({
      email:'',
      password:''
    })
    const [validated, setValidated] = useState(false);
    const formContext = useContext(FormContext);

    const validate = ()=>{
      var error=false;
      setValidated(true);
      if(formContext.state.email.length>0){
        if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(formContext.state.email))
        {
          return false
        } else {
          setErrors({
            ...formErrors,email:'Email Is Invalidated'
          })
        }
      } else {
        setErrors({
          ...formErrors,email:'Email Address Is Required'
        })
      }

      if(formContext.state.password.length>0){
        if(formContext.state.password.length<6){
          setErrors({
            ...formErrors,password:'Password Length Must Greater Than 6'
          })
        }
      } else {
        setErrors({
          ...formErrors,password:'Password Is Required'
        })
      }
    }
    const handleClose = () => {
      setShow(false);
      setValidated(false);
    };
    const handleShow = () => setShow(true);
    const handleSubmit = (event) => {
      event.preventDefault();
      event.stopPropagation();
      if(validate()){
        formContext.dispatch({type:'login',payload:formContext.state})
      }


    }
    console.log(formErrors.email.length);
    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Login
        </Button>

        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Login The Account</Modal.Title>
          </Modal.Header>

            <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <Modal.Body>

                  <Form.Group controlId="formBasicEmail">
                    <Form.Label>User Account</Form.Label>
                    <Form.Control required type="email" name="email" placeholder="Enter Email" onChange={
                      (event)=>
                        {
                          formContext.dispatch({
                            type:'changeUserInfo',
                            payload:event.currentTarget
                          })
                        }
                      }/>      
            {
              formErrors.email.length>0&&
                <Form.Control.Feedback type="invalid">
                  {formErrors.email}
                </Form.Control.Feedback>
            }


                  </Form.Group>

                  <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" name="password" placeholder="Password" onChange={
                      (event)=>
                        {
                          formContext.dispatch({
                            type:'changeUserInfo',
                            payload:event.currentTarget
                          })
                        }
                      }/>
                  </Form.Group>

                  <Form.Group controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" name="checkbox" label="Remember Me" />
                  </Form.Group>

              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button type='submit' variant="primary">Login</Button>
              </Modal.Footer>
            </Form>

        </Modal>
      </>
    );
  }
