import React, {useState,useContext} from 'react';
import { Modal,Button,Form } from 'react-bootstrap';
import {FormContext} from '../../context';

export const Login = () => {
    const [show, setShow] = useState(false);
    const [formErrors,setErrors] = useState({
      error:'',
      password:''
    })
    const [validated, setValidated] = useState(false);
    const formContext = useContext(FormContext);

    const validate = ()=>{
      let errors ={};
      setValidated(true);
      if(formContext.state.email.length>0){
        if (!/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/.test(formContext.state.email))
        {
          errors.email = 'Email Is Invalidated';
        } 
      } else {
        errors.email = 'Email Address Is Required'
      }
      if(formContext.state.password.length===0){
        errors.password = 'Password Is Required';
      }
      return errors;
    }
    const handleClose = () => {
      setShow(false);
      setValidated(false);
    };
    const handleShow = () => setShow(true);
    const handleSubmit = (event) => {
      event.preventDefault();
      event.stopPropagation();
      setErrors(validate());
    }
    console.log(formErrors.email.length);
    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Login
        </Button>

        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Login The Account</Modal.Title>
          </Modal.Header>

            <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <Modal.Body>

                  <Form.Group controlId="formBasicEmail">
                    <Form.Label>User Account</Form.Label>
                    <Form.Control required type="email" name="email" placeholder="Enter Email" onChange={
                      (event)=>
                        {
                          formContext.dispatch({
                            type:'changeUserInfo',
                            payload:event.currentTarget
                          })
                        }
                      }/>      
            {
              formErrors.email.length>0&&
                <Form.Control.Feedback type="invalid">
                  {formErrors.email}
                </Form.Control.Feedback>
            }


                  </Form.Group>

                  <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control required type="password" name="password" placeholder="Password" onChange={
                      (event)=>
                        {
                          formContext.dispatch({
                            type:'changeUserInfo',
                            payload:event.currentTarget
                          })
                        }
                      }/>

            {
              formErrors.password.length>0&&
                <Form.Control.Feedback type="invalid">
                  {formErrors.password}
                </Form.Control.Feedback>
            }

                  </Form.Group>

                  <Form.Group controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" name="checkbox" label="Remember Me" />
                  </Form.Group>

              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button type='submit' variant="primary">Login</Button>
              </Modal.Footer>
            </Form>

        </Modal>
      </>
    );
  }
